import React from "react";
import {base_url} from "../utils/Constants";

class Form extends React.Component{
    handleGetCity = (e) => {
        e.preventDefault();
        let city = e.currentTarget.city.value;
        console.log(city);
        fetch(`${base_url}?q=`)
    };


    render() {
        return (
            <form onSubmit={this.handleGetCity}>
                <input type='text' name='city' placeholder='City'/>
                <button type='submit'>Get weather</button>
            </form>
        );
    }
}

export default Form;

//https://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=d0c730745682652fa96bd909f099063f
